import React, { useState, useEffect } from 'react'; 
import axios from 'axios';

const Question = () => {
  const [sections, setSections] = useState([]);
  const [sets, setSets] = useState([]);
  const [createNewSet, setCreateNewSet] = useState(false);
  const [newSetName, setNewSetName] = useState('');
  const [setTimeLimit, setSetTimeLimit] = useState('');
  const [useCustomTimer, setUseCustomTimer] = useState(false);

  const [form, setForm] = useState({
    category: '',
    section: '',
    set: '',
    questionText: '',
    questionImage: null,
    questionAudio: null,
    options: [
      { type: 'text', content: '' },
      { type: 'text', content: '' },
      { type: 'text', content: '' },
      { type: 'text', content: '' }
    ],
    correctAnswer: 'a',
    timer: null
  });

  const categories = ['Beginner', 'Intermediate', 'Advanced'];

  const sectionMap = {
    Beginner: ['Beginner', 'Intermediate', 'Advanced', 'Pro Advanced'],
    Intermediate: ['Beginner Challenge', 'Intermediate Challenge', 'Advanced Challenge', 'Pro Challenge'],
    Advanced: ['Beginner Level', 'Intermediate Level', 'Advanced Level', 'Pro Level']
  };

  useEffect(() => {
    useEffect(() => {
  // Fetch categories on mount
  const fetchCategories = async () => {
    try {
      const res = await axios.get('http://localhost:3000/api/categories');
      setCategories(res.data); // you'll need to change categories from const to state
    } catch (err) {
      console.error(err);
    }
  };
  fetchCategories();
}, []);

useEffect(() => {
  if (form.category) {
    const fetchSections = async () => {
      try {
        const res = await axios.get(`http://localhost:3000/api/categories/${form.category}/sections`);
        setSections(res.data);
      } catch (err) {
        console.error(err);
        setSections([]);
      }
    };
    fetchSections();
  } else {
    setSections([]);
  }
}, [form.category]);

    if (form.category && form.section) {
      fetchSets(form.category, form.section);
    } else {
      setSets([]);
      setForm(prev => ({ ...prev, set: '' }));
    }
  }, [form.category, form.section]);

  const fetchSets = async (category, section) => {
    try {
 const res = await axios.get(`http://localhost:3000/api/question-papers/${category}/sections/${section}/sets`);      // normalize sets (string or object with .name)
      const fetchedSets = res.data.map(s => (typeof s === 'string' ? s : s.name));
      setSets(fetchedSets);
    } catch (err) {
      console.error('Error fetching sets:', err);
      setSets([]);
    }
  };

  const createSet = async () => {
    if (!newSetName.trim()) return alert('Set name is required');

    try {
      const payload = { setName: newSetName.trim() };
      if (setTimeLimit !== '' && setTimeLimit !== null) {
        payload.timeLimit = Number(setTimeLimit);
      }
      // **Use consistent plural 'categories' here**
await axios.post(`http://localhost:3000/api/question-papers/${form.category}/sections/${form.section}/sets`, payload);
      alert('✅ Set created successfully');
      setCreateNewSet(false);

      // Update form set value to new set
      setForm(prev => ({ ...prev, set: newSetName.trim() }));

      // Reset new set inputs
      setNewSetName('');
      setSetTimeLimit('');

      // Refetch sets after creation to update dropdown
      fetchSets(form.category, form.section);
    } catch (err) {
      console.error('❌ Failed to create set:', err);
      alert('❌ Failed to create set');
    }
  };

  const updateTimeLimit = async () => {
    try {
     await axios.patch(`http://localhost:3000/api/question-papers/${form.category}/sections/${form.section}/sets/${form.set}/time-limit`, {
  timeLimit: Number(setTimeLimit)
});

      alert('✅ Time limit updated');
    } catch (err) {
      console.error('❌ Failed to update time limit:', err);
      alert('❌ Failed to update time limit');
    }
  };

  const deleteTimeLimit = async () => {
    try {
await axios.delete(`http://localhost:3000/api/question-papers/${form.category}/sections/${form.section}/sets/${form.set}/time-limit`);
      alert('✅ Time limit deleted');
      setSetTimeLimit('');
    } catch (err) {
      console.error('❌ Failed to delete time limit:', err);
      alert('❌ Failed to delete time limit');
    }
  };

  const handleCategoryChange = (e) => {
    const selectedCategory = e.target.value;
    setForm({
      category: selectedCategory,
      section: '',
      set: '',
      questionText: '',
      questionImage: null,
      questionAudio: null,
      options: [
        { type: 'text', content: '' },
        { type: 'text', content: '' },
        { type: 'text', content: '' },
        { type: 'text', content: '' }
      ],
      correctAnswer: 'a',
      timer: null
    });
    setSections(sectionMap[selectedCategory] || []);
    setSets([]);
    setCreateNewSet(false);
    setNewSetName('');
    setSetTimeLimit('');
    setUseCustomTimer(false);
  };

  const handleSectionChange = (e) => {
    const selectedSection = e.target.value;
    setForm(prev => ({
      ...prev,
      section: selectedSection,
      set: '',
      questionText: '',
      questionImage: null,
      questionAudio: null,
      options: [
        { type: 'text', content: '' },
        { type: 'text', content: '' },
        { type: 'text', content: '' },
        { type: 'text', content: '' }
      ],
      correctAnswer: 'a',
      timer: null
    }));
    fetchSets(form.category, selectedSection);
    setCreateNewSet(false);
    setNewSetName('');
    setSetTimeLimit('');
    setUseCustomTimer(false);
  };

  const handleSetChange = async (e) => {
    const selectedSet = e.target.value;
    setForm(prev => ({ ...prev, set: selectedSet }));

    try {
      // Fetch sets to find the time limit of selected set
const res = await axios.get(`http://localhost:3000/api/question-papers/${form.category}/sections/${form.section}/sets`);
      const currentSet = res.data.find(s => (typeof s === 'string' ? s === selectedSet : s.name === selectedSet));
      if (currentSet && currentSet.timeLimit !== undefined && currentSet.timeLimit !== null) {
        setSetTimeLimit(String(currentSet.timeLimit));
        setForm(prev => ({ ...prev, timer: null }));
        setUseCustomTimer(false);
      } else {
        setSetTimeLimit('');
        setForm(prev => ({ ...prev, timer: null }));
      }
    } catch (err) {
      console.error('Error loading set time:', err);
      setSetTimeLimit('');
      setForm(prev => ({ ...prev, timer: null }));
    }
  };

  const handleOptionChange = (index, field, value, isFile = false) => {
    const updatedOptions = [...form.options];
    updatedOptions[index][field] = isFile ? value[0] : value;
    setForm({ ...form, options: updatedOptions });
  };

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (files) {
      setForm({ ...form, [name]: files[0] });
    } else {
      setForm({ ...form, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();

    formData.append('category', form.category);
    formData.append('section', form.section);
    formData.append('set', createNewSet ? newSetName.trim() : form.set);
    formData.append('questionText', form.questionText);
    if (form.questionImage) formData.append('questionImage', form.questionImage);
    if (form.questionAudio) formData.append('questionAudio', form.questionAudio);
    formData.append('correctAnswer', form.correctAnswer);
    formData.append('timer', useCustomTimer ? form.timer || 0 : setTimeLimit || 0);

    form.options.forEach((opt, idx) => {
      formData.append(`options[${idx}][type]`, opt.type);
      formData.append(`options[${idx}][content]`, opt.content);
    });

    try {
      await axios.post('http://localhost:3000/api/questions/create', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      alert('✅ Question created with file uploads!');
      // Reset form if needed here
    } catch (err) {
      console.error('❌ Error submitting question:', err);
      alert('❌ Error submitting question');
    }
  };

  return (
    <div className="max-w-4xl p-6 mx-auto bg-white rounded shadow">
      <h2 className="mb-4 text-2xl font-bold">Create Question</h2>
      <form onSubmit={handleSubmit} className="space-y-4">

        {/* Category */}
        <div>
          <label className="block font-medium">Category</label>
          <select name="category" value={form.category} onChange={handleCategoryChange} className="w-full p-2 border rounded">
            <option value="">-- Select Category --</option>
            {categories.map(cat => (
              <option key={cat} value={cat}>{cat}</option>
            ))}
          </select>
        </div>

        {/* Section */}
        {sections.length > 0 && (
          <div>
            <label className="block font-medium">Section</label>
            <select name="section" value={form.section} onChange={handleSectionChange} className="w-full p-2 border rounded">
              <option value="">-- Select Section --</option>
              {sections.map(sec => (
                <option key={sec} value={sec}>{sec}</option>
              ))}
            </select>
          </div>
        )}

        {/* Set */}
        {form.section && (
          <div>
            <label className="block font-medium">Set</label>
            {!createNewSet ? (
              <>
                <select name="set" value={form.set} onChange={handleSetChange} className="w-full p-2 border rounded">
                  <option value="">-- Select Set --</option>
                  {sets.map(s => (
                    <option key={s} value={s}>{s}</option>
                  ))}
                </select>
                <button type="button" onClick={() => {
                  setCreateNewSet(true);
                  setNewSetName('');
                  setSetTimeLimit('');
                }} className="mt-2 text-sm text-blue-600 underline">
                  + Create New Set
                </button>

                {form.set && (
                  <div className="mt-2 space-y-2">
                    <input
                      type="number"
                      placeholder="Update time limit in seconds"
                      value={setTimeLimit || ''}
                      onChange={(e) => setSetTimeLimit(e.target.value)}
                      className="w-full p-2 border rounded"
                      min="0"
                    />
                    <div className="flex gap-4">
                      <button type="button" onClick={updateTimeLimit} className="px-3 py-1 text-white bg-blue-600 rounded">Update Time Limit</button>
                      <button type="button" onClick={deleteTimeLimit} className="px-3 py-1 text-white bg-red-600 rounded">Delete Time Limit</button>
                    </div>
                  </div>
                )}
              </>
            ) : (
              <div className="mt-2 space-y-2">
                <input
                  type="text"
                  placeholder="Enter new set name"
                  value={newSetName}
                  onChange={(e) => setNewSetName(e.target.value)}
                  className="w-full p-2 border rounded"
                />
                <input
                  type="number"
                  placeholder="Optional: Set time limit in seconds"
                  value={setTimeLimit || ''}
                  onChange={(e) => setSetTimeLimit(e.target.value)}
                  className="w-full p-2 border rounded"
                  min="0"
                />
                <div className="flex gap-4">
                  <button type="button" onClick={createSet} className="px-3 py-1 text-white bg-green-600 rounded">Create Set</button>
                  <button type="button" onClick={() => setCreateNewSet(false)} className="text-sm text-gray-500 underline">Cancel</button>
                </div>
              </div>
            )}
          </div>
        )}

        {/* Question Text */}
        <div>
          <label className="block font-medium">Question Text</label>
          <input type="text" name="questionText" value={form.questionText} onChange={handleChange} className="w-full p-2 border rounded" />
        </div>

        {/* Question Image */}
        <div>
          <label className="block font-medium">Question Image</label>
          <input type="file" name="questionImage" accept="image/*" onChange={handleChange} className="w-full p-2 border rounded" />
        </div>

        {/* Question Audio */}
        <div>
          <label className="block font-medium">Question Audio</label>
          <input type="file" name="questionAudio" accept="audio/*" onChange={handleChange} className="w-full p-2 border rounded" />
        </div>

        {/* Options */}
        <div>
          <label className="block font-medium">Options</label>
          {form.options.map((option, idx) => (
            <div key={idx} className="flex items-center gap-2 mb-2">
              <select
                value={option.type}
                onChange={(e) => handleOptionChange(idx, 'type', e.target.value)}
                className="p-2 border rounded"
              >
                <option value="text">Text</option>
                <option value="image">Image</option>
                <option value="audio">Audio</option>
              </select>
              {option.type === 'text' ? (
                <input
                  type="text"
                  placeholder={`Option ${String.fromCharCode(97 + idx)}`}
                  value={option.content}
                  onChange={(e) => handleOptionChange(idx, 'content', e.target.value)}
                  className="w-full p-2 border rounded"
                />
              ) : (
                <input
                  type="file"
                  accept={option.type === 'image' ? 'image/*' : 'audio/*'}
                  onChange={(e) => handleOptionChange(idx, 'content', e.target.files, true)}
                  className="w-full p-2 border rounded"
                />
              )}
            </div>
          ))}
        </div>

        {/* Correct Answer */}
        <div>
          <label className="block font-medium">Correct Answer</label>
          <select name="correctAnswer" value={form.correctAnswer} onChange={handleChange} className="w-full p-2 border rounded">
            <option value="a">A</option>
            <option value="b">B</option>
            <option value="c">C</option>
            <option value="d">D</option>
          </select>
        </div>

        {/* Timer */}
        <div>
          <label className="block font-medium">Timer</label>
          {setTimeLimit !== '' && !useCustomTimer && (
            <p className="mb-1 text-sm text-gray-600">
              Using set timer: <strong>{setTimeLimit} seconds</strong>
            </p>
          )}
          <div className="flex items-center gap-2">
            <input
              type="checkbox"
              checked={useCustomTimer}
              onChange={() => {
                setUseCustomTimer(!useCustomTimer);
                if (!useCustomTimer) {
                  setForm(prev => ({ ...prev, timer: setTimeLimit || '' }));
                } else {
                  setForm(prev => ({ ...prev, timer: null }));
                }
              }}
            />
            <span className="text-sm">Use custom timer</span>
          </div>
          {useCustomTimer && (
            <input
              type="number"
              name="timer"
              value={form.timer || ''}
              onChange={handleChange}
              className="w-full p-2 mt-1 border rounded"
              placeholder="Enter custom timer in seconds"
              min="0"
            />
          )}
        </div>

        {/* Submit */}
        <button type="submit" className="px-4 py-2 text-white bg-blue-600 rounded hover:bg-blue-700">
          Submit Question
        </button>
      </form>
    </div>
  );
};

export default Question;
