import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Question = () => {
  const [sections, setSections] = useState([]);
  const [sets, setSets] = useState([]);
  const [createNewSet, setCreateNewSet] = useState(false);
  const [newSetName, setNewSetName] = useState('');
  const [setTimeLimit, setSetTimeLimit] = useState(null);
  const [useCustomTimer, setUseCustomTimer] = useState(false);

  const [form, setForm] = useState({
    category: '',
    section: '',
    set: '',
    questionText: '',
    questionImage: null,
    questionAudio: null,
    options: [
      { type: 'text', content: '' },
      { type: 'text', content: '' },
      { type: 'text', content: '' },
      { type: 'text', content: '' }
    ],
    correctAnswer: 'a',
    timer: null
  });

  const categories = ['Beginner', 'Intermediate', 'Advanced'];

  const sectionMap = {
    Beginner: ['Beginner', 'Intermediate', 'Advanced', 'Pro Advanced'],
    Intermediate: ['Beginner Challenge', 'Intermediate Challenge', 'Advanced Challenge', 'Pro Challenge'],
    Advanced: ['Beginner Level', 'Intermediate Level', 'Advanced Level', 'Pro Level']
  };

  useEffect(() => {}, []);

  const handleCategoryChange = (e) => {
    const selectedCategory = e.target.value;
    setForm({ ...form, category: selectedCategory, section: '', set: '' });
    setSections(sectionMap[selectedCategory] || []);
    setSets([]);
    setCreateNewSet(false);
  };

  const handleSetChange = async (e) => {
    const selectedSet = e.target.value;
    setForm({ ...form, set: selectedSet });
    try {
      const res = await axios.get(`http://localhost:3000/api/${form.category}/${form.section}/sets`);
      const currentSet = res.data.find(s => s.name === selectedSet);
      if (currentSet?.timeLimit) {
        setSetTimeLimit(currentSet.timeLimit);
        setForm(prev => ({ ...prev, timer: null }));
      } else {
        setSetTimeLimit(null);
      }
    } catch (err) {
      console.error('Error loading set time:', err);
      setSetTimeLimit(null);
    }
  };

  const handleOptionChange = (index, field, value, isFile = false) => {
    const updatedOptions = [...form.options];
    updatedOptions[index][field] = isFile ? value[0] : value;
    setForm({ ...form, options: updatedOptions });
  };

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (files) {
      setForm({ ...form, [name]: files[0] });
    } else {
      setForm({ ...form, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();

    formData.append('category', form.category);
    formData.append('section', form.section);
    formData.append('set', createNewSet ? newSetName : form.set);
    formData.append('questionText', form.questionText);
    if (form.questionImage) formData.append('questionImage', form.questionImage);
    if (form.questionAudio) formData.append('questionAudio', form.questionAudio);
    formData.append('correctAnswer', form.correctAnswer);
    formData.append('timer', useCustomTimer ? form.timer : setTimeLimit);

    form.options.forEach((opt, idx) => {
      formData.append(`options[${idx}][type]`, opt.type);
      formData.append(`options[${idx}][content]`, opt.content);
    });

    try {
      await axios.post('http://localhost:3000/api/questions/create', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      alert('✅ Question created with file uploads!');
    } catch (err) {
      console.error('❌ Error submitting question:', err);
    }
  };

  const handleSectionChange = async (e) => {
    const selectedSection = e.target.value;
    setForm(prev => ({ ...prev, section: selectedSection, set: '' }));
    try {
      const res = await axios.get(`http://localhost:3000/api/${form.category}/${selectedSection}/sets`);
      const fetchedSets = res.data.map(s => (typeof s === 'string' ? s : s.name));
      setSets(fetchedSets);
    } catch (err) {
      console.error('Error fetching sets:', err);
    }
  };

  return (
    <div className="max-w-4xl p-6 mx-auto bg-white rounded shadow">
      <h2 className="mb-4 text-2xl font-bold">Create Question</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block font-medium">Category</label>
          <select name="category" value={form.category} onChange={handleCategoryChange} className="w-full p-2 border rounded">
            <option value="">-- Select Category --</option>
            {categories.map(cat => (
              <option key={cat} value={cat}>{cat}</option>
            ))}
          </select>
        </div>

        {sections.length > 0 && (
          <div>
            <label className="block font-medium">Section</label>
            <select name="section" value={form.section} onChange={handleSectionChange} className="w-full p-2 border rounded">
              <option value="">-- Select Section --</option>
              {sections.map(sec => (
                <option key={sec} value={sec}>{sec}</option>
              ))}
            </select>
          </div>
        )}

        {form.section && (
          <div>
            <label className="block font-medium">Set</label>
            {!createNewSet && (
              <>
                <select name="set" value={form.set} onChange={handleSetChange} className="w-full p-2 border rounded">
                  <option value="">-- Select Set --</option>
                  {sets.map(s => (
                    <option key={s} value={s}>{s}</option>
                  ))}
                </select>
                <button
                  type="button"
                  onClick={() => setCreateNewSet(true)}
                  className="mt-2 text-sm text-blue-600 underline"
                >
                  + Create New Set
                </button>
              </>
            )}
            {createNewSet && (
              <div className="mt-2">
                <input
                  type="text"
                  placeholder="Enter new set name"
                  value={newSetName}
                  onChange={(e) => setNewSetName(e.target.value)}
                  className="w-full p-2 border rounded"
                />
                <button
                  type="button"
                  onClick={() => setCreateNewSet(false)}
                  className="mt-1 text-sm text-gray-500 underline"
                >
                  Cancel new set
                </button>
              </div>
            )}
          </div>
        )}

        <div>
          <label className="block font-medium">Question Text</label>
          <input type="text" name="questionText" value={form.questionText} onChange={handleChange} className="w-full p-2 border rounded" />
        </div>

        <div>
          <label className="block font-medium">Question Image</label>
          <input type="file" name="questionImage" accept="image/*" onChange={handleChange} className="w-full p-2 border rounded" />
        </div>

        <div>
          <label className="block font-medium">Question Audio</label>
          <input type="file" name="questionAudio" accept="audio/*" onChange={handleChange} className="w-full p-2 border rounded" />
        </div>

        <div>
          <label className="block font-medium">Options</label>
          {form.options.map((option, idx) => (
            <div key={idx} className="flex items-center gap-2 mb-2">
              <select
                value={option.type}
                onChange={(e) => handleOptionChange(idx, 'type', e.target.value)}
                className="p-2 border rounded"
              >
                <option value="text">Text</option>
                <option value="image">Image</option>
                <option value="audio">Audio</option>
              </select>
              {option.type === 'text' ? (
                <input
                  type="text"
                  placeholder={`Option ${String.fromCharCode(97 + idx)}`}
                  value={option.content}
                  onChange={(e) => handleOptionChange(idx, 'content', e.target.value)}
                  className="w-full p-2 border rounded"
                />
              ) : (
                <input
                  type="file"
                  accept={option.type === 'image' ? 'image/*' : 'audio/*'}
                  onChange={(e) => handleOptionChange(idx, 'content', e.target.files, true)}
                  className="w-full p-2 border rounded"
                />
              )}
            </div>
          ))}
        </div>

        <div>
          <label className="block font-medium">Correct Answer</label>
          <select name="correctAnswer" value={form.correctAnswer} onChange={handleChange} className="w-full p-2 border rounded">
            <option value="a">A</option>
            <option value="b">B</option>
            <option value="c">C</option>
            <option value="d">D</option>
          </select>
        </div>

        <div>
          <label className="block font-medium">Timer</label>
          {setTimeLimit !== null && !useCustomTimer && (
            <p className="mb-1 text-sm text-gray-600">
              Using set timer: <strong>{setTimeLimit} seconds</strong>
            </p>
          )}
          <div className="flex items-center gap-2">
            <input
              type="checkbox"
              checked={useCustomTimer}
              onChange={() => {
                setUseCustomTimer(!useCustomTimer);
                if (!useCustomTimer) {
                  setForm(prev => ({ ...prev, timer: setTimeLimit || '' }));
                } else {
                  setForm(prev => ({ ...prev, timer: null }));
                }
              }}
            />
            <span className="text-sm">Use custom timer</span>
          </div>
          {useCustomTimer && (
            <input
              type="number"
              name="timer"
              value={form.timer || ''}
              onChange={handleChange}
              className="w-full p-2 mt-1 border rounded"
              placeholder="Enter custom timer in seconds"
            />
          )}
        </div>

        <button type="submit" className="px-4 py-2 text-white bg-blue-600 rounded hover:bg-blue-700">
          Submit Question
        </button>
      </form>
    </div>
  );
};

export default Question;
