import React, { useState, useEffect } from 'react'; 
import axios from 'axios';

export default function AdminQuestionForm() {
  const [question, setQuestion] = useState({
    section: '',
    questionText: '',
    options: [
      { type: 'text', content: '' },
      { type: 'text', content: '' },
      { type: 'text', content: '' },
      { type: 'text', content: '' }
    ],
    correctAnswer: ''
  });

  const [sections, setSections] = useState([]);
  const [newSection, setNewSection] = useState('');
  const [viewQuestions, setViewQuestions] = useState([]);

  useEffect(() => {
    fetchSections();
  }, []);

  const fetchSections = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/sections');
      setSections(res.data); // Assuming the response is an array of sections
    } catch (error) {
      console.error('Error fetching sections:', error);
      alert('Failed to fetch sections');
    }
  };

  const handleOptionChange = (index, field, value) => {
    const newOptions = [...question.options];
    newOptions[index][field] = value;
    setQuestion({ ...question, options: newOptions });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!question.section || !question.questionText || !question.correctAnswer) {
      alert('Please fill in all fields');
      return;
    }

    // Ensure the correctAnswer is one of the options (a/b/c/d)
    const validAnswers = ['a', 'b', 'c', 'd'];
    if (!validAnswers.includes(question.correctAnswer)) {
      alert('Please select a valid correct answer (a/b/c/d)');
      return;
    }
    try {
      await axios.post('http://localhost:5000/api/questions/create', question);
      alert('Question created!');
      setQuestion({
        section: '',
        questionText: '',
        options: [
          { type: 'text', content: '' },
          { type: 'text', content: '' },
          { type: 'text', content: '' },
          { type: 'text', content: '' }
        ],
        correctAnswer: '' });
      setViewQuestions([]);
    } catch (error) {
      console.error('Error creating question:', error);
      alert('Failed to create question');
    }  };
  const handleAddSection = async () => {
    if (!newSection.trim()) return;
    try {
      await axios.post('http://localhost:5000/api/sections/create', { name: newSection });
      setNewSection('');
      fetchSections();
    } catch (error) {
      console.error('Error adding section:', error);
      alert('Failed to add section');
    }
  };

  const handleDeleteSection = async (sectionToDelete) => {
    try {
      await axios.delete(`http://localhost:5000/api/sections/${sectionToDelete}`);  // Fixed template literal here
      fetchSections();
      if (question.section === sectionToDelete) {
        setQuestion({ ...question, section: '' });
        setViewQuestions([]); // Optionally keep the questions or reset as necessary
      }
    } catch (error) {
      console.error('Error deleting section:', error);
      alert('Failed to delete section');
    }
  };
  const handleSectionReview = async () => {
    if (!question.section) {
      alert('Please select a section');
      return;
    }
    try {
      const res = await axios.get(`http://localhost:5000/api/questions/by-section/${question.section}`);  // Fixed template literal here
      setViewQuestions(res.data);
    } catch (error) {
      console.error('Error fetching questions for section:', error);
      alert('Failed to fetch questions for the section');
    }
  };
  return (
    <div className="space-y-6">
      {/* Section Management */}
      <div className="space-y-2">
        <input
          value={newSection}
          onChange={(e) => setNewSection(e.target.value)}
          className="p-2 border"
          placeholder="New Section Name"
        />
        <button onClick={handleAddSection} className="px-4 py-2 text-white bg-purple-600 rounded">
          Add Section
        </button>
        <div className="mt-2 space-y-1">
          {sections.length > 0 ? (
            sections.map((sec, i) => (
              <div key={i} className="flex items-center justify-between p-2 border rounded">
              <span className="font-semibold text-purple-700">{sec.name}</span> {/* Colored section name */}
              <button
                onClick={() => handleDeleteSection(sec._id)}
                className="px-2 py-1 text-sm text-white bg-red-500 rounded"
              >
                Delete
              </button>
            </div>
            ))
          ) : (
            <p>No sections available</p>
          )}
        </div>
      </div>

      {/* Question Form */}
      <form onSubmit={handleSubmit} className="space-y-4">
        <select
          className="w-full p-2 border"
          value={question.section}
          onChange={(e) => setQuestion({ ...question, section: e.target.value })}
        >
          <option value="">Select Section</option>
          {sections.length > 0 ? (
            sections.map((sec, i) => (
              <option key={i} value={sec._id}>{sec.name}</option>
            ))
          ) : (
            <option disabled>No sections available</option>
          )}
        </select>
        <input
          className="w-full p-2 border"
          placeholder="Question Text"
          value={question.questionText}
          onChange={e => setQuestion({ ...question, questionText: e.target.value })}
        />
        {question.options.map((opt, i) => (
          <div key={i} className="flex gap-2">
            <select onChange={e => handleOptionChange(i, 'type', e.target.value)} value={opt.type}>
              <option value="text">Text</option>
              <option value="image">Image</option>
              <option value="audio">Audio</option>
            </select>
            {opt.type === 'text' ? (
  <input
    className="p-2 border"
    placeholder={`Option ${String.fromCharCode(65 + i)}`}
    value={opt.content}
    onChange={e => handleOptionChange(i, 'content', e.target.value)}
  />
) : (
  <>
    <input
      type="file"
      accept={opt.type === 'image' ? 'image/*' : 'audio/*'}
      onChange={(e) => {
        const file = e.target.files[0];
        if (file) {
          const reader = new FileReader();
          reader.onloadend = () => {
            handleOptionChange(i, 'content', reader.result); // store base64 string
          };
          reader.readAsDataURL(file);
        }
      }}
    />
    {/* Optional preview */}
    {opt.content && opt.type === 'image' && (
      <img src={opt.content} alt="Preview" className="w-16 h-16 mt-1 rounded" />
    )}
    {opt.content && opt.type === 'audio' && (
      <audio controls src={opt.content} className="mt-1" />
    )}
  </>
)}

          </div>
        ))}

        <input
          className="w-full p-2 border"
          placeholder="Correct Answer (a/b/c/d)"
          value={question.correctAnswer}
          onChange={e => setQuestion({ ...question, correctAnswer: e.target.value })}
        />

        <button type="submit" className="px-4 py-2 text-white bg-green-500 rounded">
          Submit Question
        </button>
      </form>

      {/* Review Section */}
      <div>
        <button
          onClick={handleSectionReview}
          className="px-4 py-2 mt-4 text-white bg-blue-500 rounded"
          disabled={!question.section}
        >
          Review Question Paper
        </button>

        {viewQuestions.length > 0 && (
          <div className="mt-4">
            <h2 className="text-lg font-semibold">Section: {question.section}</h2>
            <ul className="mt-2 space-y-2">
              {viewQuestions.map((q, idx) => (
                <li key={q._id} className="p-2 border rounded">
                  {idx + 1}. {q.questionText}
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}
