import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';

// Move sectionMap outside the component as it's static data
// IMPORTANT: Ensure these section names match your backend's sectionSchema enum exactly.
const sectionMap = {
  Beginner: ['Beginner', 'Intermediate', 'Advanced', 'Pro Advanced'],
  Intermediate: ['Beginner Challenge', 'Intermediate Challenge', 'Advanced Challenge', 'Pro Advanced Challenge'],
  Advanced: ['Beginner Level', 'Intermediate Level', 'Advanced Level', 'Pro Advanced Level']
};

const Question = () => {
  const [sections, setSections] = useState([]);
  const [sets, setSets] = useState([]);
  const [createNewSet, setCreateNewSet] = useState(false);
  const [newSetName, setNewSetName] = useState('');
  const [setTimeLimit, setSetTimeLimit] = useState('');

  const [form, setForm] = useState({
    category: '',
    section: '',
    set: '',
    questionText: '',
    questionImage: null,
    questionAudio: null,
    options: [
      { type: 'text', content: '' },
      { type: 'text', content: '' },
      { type: 'text', content: '' },
      { type: 'text', content: '' }
    ],
    correctAnswer: 'a',
  });

  const categories = ['Beginner', 'Intermediate', 'Advanced'];

  // Memoize fetchSets to prevent re-creating on every render
  const fetchSets = useCallback(async (category, section) => {
    try {
      const res = await axios.get(`http://localhost:3000/api/question-papers/${category}/sections/${section}/sets`);
      setSets(res.data);
      if (form.set && !res.data.some(s => s.name === form.set)) {
        setForm(prev => ({ ...prev, set: '' }));
        setSetTimeLimit('');
      } else if (form.set) {
        const currentSet = res.data.find(s => s.name === form.set);
        if (currentSet) {
          setSetTimeLimit(String(currentSet.timeLimitMinutes || ''));
        } else {
          setSetTimeLimit('');
        }
      }
    } catch (err) {
      console.error('Error fetching sets:', err.response ? err.response.data : err);
      setSets([]);
      setForm(prev => ({ ...prev, set: '' }));
      setSetTimeLimit('');
    }
  }, [form.set]);

  // Fetch sections based on category
  useEffect(() => {
    if (form.category) {
      // sectionMap is now outside, so it's a stable reference.
      // We no longer need to include sectionMap in the dependencies here.
      setSections(sectionMap[form.category] || []);
      setForm(prev => ({ ...prev, section: '', set: '' }));
      setSets([]);
    } else {
      setSections([]);
      setSets([]);
      setForm(prev => ({ ...prev, section: '', set: '' }));
    }
    setCreateNewSet(false);
    setNewSetName('');
    setSetTimeLimit('');
  }, [form.category]); // Dependencies are now just form.category

  // Fetch sets when category or section changes
  useEffect(() => {
    if (form.category && form.section) {
      fetchSets(form.category, form.section);
    } else {
      setSets([]);
      setForm(prev => ({ ...prev, set: '' }));
      setSetTimeLimit('');
    }
  }, [form.category, form.section, fetchSets]);

  const createSet = async () => {
    if (!newSetName.trim()) return alert('Set name is required');

    try {
      const payload = { name: newSetName.trim() };
      if (setTimeLimit !== '' && setTimeLimit !== null) {
        payload.timeLimitMinutes = Number(setTimeLimit);
      }

      await axios.post(`http://localhost:3000/api/question-papers/${form.category}/sections/${form.section}/sets`, payload);
      alert('✅ Set created successfully');
      setCreateNewSet(false);

      setForm(prev => ({ ...prev, set: newSetName.trim() }));

      setNewSetName('');
      setSetTimeLimit('');

      fetchSets(form.category, form.section);
    } catch (err) {
      console.error('❌ Failed to create set:', err.response ? err.response.data : err);
      alert(`❌ Failed to create set: ${err.response ? err.response.data.message : err.message}`);
    }
  };

  const updateTimeLimit = async () => {
    if (!form.set) {
      return alert('Please select a set to update its time limit.');
    }
    try {
      await axios.patch(
        `http://localhost:3000/api/question-papers/${form.category}/sections/${form.section}/sets/${form.set}/time-limit`,
        { timeLimitMinutes: Number(setTimeLimit) }
      );
      alert('✅ Time limit updated');
      fetchSets(form.category, form.section);
    } catch (err) {
      console.error('❌ Failed to update time limit:', err.response ? err.response.data : err);
      alert(`❌ Failed to update time limit: ${err.response ? err.response.data.message : err.message}`);
    }
  };

  const deleteTimeLimit = async () => {
    if (!form.set) {
      return alert('Please select a set to delete its time limit.');
    }
    try {
      await axios.delete(
        `http://localhost:3000/api/question-papers/${form.category}/sections/${form.section}/sets/${form.set}/time-limit`
      );
      alert('✅ Time limit deleted');
      setSetTimeLimit('');
      fetchSets(form.category, form.section);
    } catch (err) {
      console.error('❌ Failed to delete time limit:', err.response ? err.response.data : err);
      alert(`❌ Failed to delete time limit: ${err.response ? err.response.data.message : err.message}`);
    }
  };

  const handleCategoryChange = (e) => {
    const selectedCategory = e.target.value;
    setForm(prev => ({
      ...prev,
      category: selectedCategory,
      section: '',
      set: '',
      questionText: '',
      questionImage: null,
      questionAudio: null,
      options: [
        { type: 'text', content: '' },
        { type: 'text', content: '' },
        { type: 'text', content: '' },
        { type: 'text', content: '' }
      ],
      correctAnswer: 'a',
    }));
    setCreateNewSet(false);
    setNewSetName('');
    setSetTimeLimit('');
  };

  const handleSectionChange = (e) => {
    const selectedSection = e.target.value;
    setForm(prev => ({
      ...prev,
      section: selectedSection,
      set: '',
      questionText: '',
      questionImage: null,
      questionAudio: null,
      options: [
        { type: 'text', content: '' },
        { type: 'text', content: '' },
        { type: 'text', content: '' },
        { type: 'text', content: '' }
      ],
      correctAnswer: 'a',
    }));
    setCreateNewSet(false);
    setNewSetName('');
    setSetTimeLimit('');
  };

  const handleSetChange = async (e) => {
    const selectedSetName = e.target.value;
    setForm(prev => ({ ...prev, set: selectedSetName }));

    const currentSet = sets.find(s => s.name === selectedSetName);
    if (currentSet) {
      setSetTimeLimit(String(currentSet.timeLimitMinutes || ''));
    } else {
      setSetTimeLimit('');
    }
  };

  const handleOptionChange = (index, field, value, isFile = false) => {
    const updatedOptions = [...form.options];
    updatedOptions[index][field] = isFile ? value : value;
    setForm({ ...form, options: updatedOptions });
  };

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (files && files.length > 0) {
      setForm({ ...form, [name]: files[0] });
    } else {
      setForm({ ...form, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!form.category || !form.section || !form.set || !form.questionText || form.options.some(opt => !opt.content) || !form.correctAnswer) {
      return alert('Please fill in all required fields (Category, Section, Set, Question Text, Options, Correct Answer).');
    }

    const formData = new FormData();

    formData.append('category', form.category);
    formData.append('section', form.section);
    formData.append('set', form.set);
    formData.append('questionText', form.questionText);
    if (form.questionImage) formData.append('questionImage', form.questionImage);
    if (form.questionAudio) formData.append('questionAudio', form.questionAudio);
    formData.append('correctAnswer', form.correctAnswer);

    form.options.forEach((opt, idx) => {
      const contentValue = opt.type === 'text' ? opt.content : (opt.content instanceof File ? opt.content : null);
      if (contentValue) {
        formData.append(`options[${idx}][type]`, opt.type);
        formData.append(`options[${idx}][content]`, contentValue);
      }
    });

    try {
      await axios.post('http://localhost:3000/api/questions/create', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      alert('✅ Question created successfully!');
      setForm({
        category: '',
        section: '',
        set: '',
        questionText: '',
        questionImage: null,
        questionAudio: null,
        options: [
          { type: 'text', content: '' },
          { type: 'text', content: '' },
          { type: 'text', content: '' },
          { type: 'text', content: '' }
        ],
        correctAnswer: 'a',
      });
      setSections([]);
      setSets([]);
      setCreateNewSet(false);
      setNewSetName('');
      setSetTimeLimit('');
    } catch (err) {
      console.error('❌ Error submitting question:', err.response ? err.response.data : err);
      alert(`❌ Error submitting question: ${err.response ? err.response.data.message : err.message}`);
    }
  };

  return (
    <div className="max-w-4xl p-6 mx-auto bg-white rounded shadow">
      <h2 className="mb-4 text-2xl font-bold">Create Question</h2>
      <form onSubmit={handleSubmit} className="space-y-4">

        {/* Category */}
        <div>
          <label className="block font-medium">Category</label>
          <select name="category" value={form.category} onChange={handleCategoryChange} className="w-full p-2 border rounded">
            <option value="">-- Select Category --</option>
            {categories.map(cat => (
              <option key={cat} value={cat}>{cat}</option>
            ))}
          </select>
        </div>

        {/* Section */}
        {form.category && sections.length > 0 && (
          <div>
            <label className="block font-medium">Section</label>
            <select name="section" value={form.section} onChange={handleSectionChange} className="w-full p-2 border rounded">
              <option value="">-- Select Section --</option>
              {sections.map(sec => (
                <option key={sec} value={sec}>{sec}</option>
              ))}
            </select>
          </div>
        )}

        {/* Set */}
        {form.section && (
          <div>
            <label className="block font-medium">Set</label>
            {!createNewSet ? (
              <>
                <select name="set" value={form.set} onChange={handleSetChange} className="w-full p-2 border rounded">
                  <option value="">-- Select Set --</option>
                  {sets.map(s => (
                    <option key={s.name} value={s.name}>{s.name}</option>
                  ))}
                </select>
                <button type="button" onClick={() => {
                  setCreateNewSet(true);
                  setNewSetName('');
                  setSetTimeLimit('');
                }} className="mt-2 text-sm text-blue-600 underline">
                  + Create New Set
                </button>

                {form.set && (
                  <div className="mt-2 space-y-2">
                    <label className="block text-sm font-medium">Set Time Limit (in minutes)</label>
                    <input
                      type="number"
                      placeholder="Update time limit in minutes"
                      value={setTimeLimit || ''}
                      onChange={(e) => setSetTimeLimit(e.target.value)}
                      className="w-full p-2 border rounded"
                      min="0"
                    />
                    <div className="flex gap-4">
                      <button type="button" onClick={updateTimeLimit} className="px-3 py-1 text-white bg-blue-600 rounded">Update Time Limit</button>
                      <button type="button" onClick={deleteTimeLimit} className="px-3 py-1 text-white bg-red-600 rounded">Delete Time Limit</button>
                    </div>
                  </div>
                )}
              </>
            ) : (
              <div className="mt-2 space-y-2">
                <input
                  type="text"
                  placeholder="Enter new set name"
                  value={newSetName}
                  onChange={(e) => setNewSetName(e.target.value)}
                  className="w-full p-2 border rounded"
                />
                <input
                  type="number"
                  placeholder="Optional: Set time limit in minutes"
                  value={setTimeLimit || ''}
                  onChange={(e) => setSetTimeLimit(e.target.value)}
                  className="w-full p-2 border rounded"
                  min="0"
                />
                <div className="flex gap-4">
                  <button type="button" onClick={createSet} className="px-3 py-1 text-white bg-green-600 rounded">Create Set</button>
                  <button type="button" onClick={() => setCreateNewSet(false)} className="text-sm text-gray-500 underline">Cancel</button>
                </div>
              </div>
            )}
          </div>
        )}

        {/* Question Text */}
        <div>
          <label className="block font-medium">Question Text</label>
          <input type="text" name="questionText" value={form.questionText} onChange={handleChange} className="w-full p-2 border rounded" />
        </div>

        {/* Question Image */}
        <div>
          <label className="block font-medium">Question Image</label>
          <input type="file" name="questionImage" accept="image/*" onChange={handleChange} className="w-full p-2 border rounded" />
        </div>

        {/* Question Audio */}
        <div>
          <label className="block font-medium">Question Audio</label>
          <input type="file" name="questionAudio" accept="audio/*" onChange={handleChange} className="w-full p-2 border rounded" />
        </div>

        {/* Options */}
        <div>
          <label className="block font-medium">Options</label>
          {form.options.map((option, idx) => (
            <div key={idx} className="flex items-center gap-2 mb-2">
              <select
                value={option.type}
                onChange={(e) => handleOptionChange(idx, 'type', e.target.value)}
                className="p-2 border rounded"
              >
                <option value="text">Text</option>
                <option value="image">Image</option>
                <option value="audio">Audio</option>
              </select>
              {option.type === 'text' ? (
                <input
                  type="text"
                  placeholder={`Option ${String.fromCharCode(97 + idx)}`}
                  value={option.content}
                  onChange={(e) => handleOptionChange(idx, 'content', e.target.value)}
                  className="w-full p-2 border rounded"
                />
              ) : (
                <input
                  type="file"
                  accept={option.type === 'image' ? 'image/*' : 'audio/*'}
                  onChange={(e) => handleOptionChange(idx, 'content', e.target.files[0], true)}
                  className="w-full p-2 border rounded"
                />
              )}
            </div>
          ))}
        </div>

        {/* Correct Answer */}
        <div>
          <label className="block font-medium">Correct Answer</label>
          <select name="correctAnswer" value={form.correctAnswer} onChange={handleChange} className="w-full p-2 border rounded">
            {form.options.map((_, idx) => (
              <option key={String.fromCharCode(97 + idx)} value={String.fromCharCode(97 + idx)}>
                {String.fromCharCode(65 + idx)}
              </option>
            ))}
          </select>
        </div>

        {/* Timer - This section is primarily for displaying and managing set time limit, not for question submission */}
        <div>
          <label className="block font-medium">Associated Set Timer</label>
          {setTimeLimit && (
            <p className="mb-1 text-sm text-gray-600">
              Set's default timer: <strong>{setTimeLimit} minutes</strong>
            </p>
          )}
          {!setTimeLimit && form.set && (
             <p className="mb-1 text-sm text-gray-600">
               This set has no default time limit.
             </p>
          )}
          <p className="mb-1 text-sm text-gray-600">
             The time limit for this question is determined by its selected set.
          </p>
        </div>

        {/* Submit */}
        <button type="submit" className="px-4 py-2 text-white bg-blue-600 rounded hover:bg-blue-700">
          Submit Question
        </button>
      </form>
    </div>
  );
};

export default Question;
