// src/components/AdminQuestionForm.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function AdminQuestionForm() {
  const [question, setQuestion] = useState({
    section: '',
    questionText: '',
    options: [
      { type: 'text', content: '' },
      { type: 'text', content: '' },
      { type: 'text', content: '' },
      { type: 'text', content: '' }
    ],
    correctAnswer: ''
  });

  const [sections, setSections] = useState([]);
  const [viewQuestions, setViewQuestions] = useState([]);

  useEffect(() => {
    fetchSections();
  }, []);

  const fetchSections = async () => {
    const res = await axios.get('http://localhost:5000/api/sections');
    setSections(res.data);
  };

  const handleOptionChange = (index, field, value) => {
    const newOptions = [...question.options];
    newOptions[index][field] = value;
    setQuestion({ ...question, options: newOptions });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios.post('http://localhost:5000/api/questions/create', question);
    alert('Question created!');
  };

  const handleSectionReview = async () => {
    const res = await axios.get(`http://localhost:5000/api/questions/by-section/${question.section}`);
    setViewQuestions(res.data);
  };

  return (
    <div className="space-y-6">
      <form onSubmit={handleSubmit} className="space-y-4">
        <select className="w-full p-2 border" onChange={e => setQuestion({ ...question, section: e.target.value })}>
          <option value="">Select Section</option>
          {sections.map((sec, i) => (
            <option key={i} value={sec}>{sec}</option>
          ))}
        </select>

        <input className="w-full p-2 border" placeholder="Question Text" onChange={e => setQuestion({ ...question, questionText: e.target.value })} />

        {question.options.map((opt, i) => (
          <div key={i} className="flex gap-2">
            <select onChange={e => handleOptionChange(i, 'type', e.target.value)}>
              <option value="text">Text</option>
              <option value="image">Image</option>
              <option value="audio">Audio</option>
            </select>
            <input className="p-2 border" placeholder={`Option ${String.fromCharCode(65 + i)}`} onChange={e => handleOptionChange(i, 'content', e.target.value)} />
          </div>
        ))}

        <input className="w-full p-2 border" placeholder="Correct Answer (a/b/c/d)" onChange={e => setQuestion({ ...question, correctAnswer: e.target.value })} />
        <button type="submit" className="px-4 py-2 text-white bg-green-500 rounded">Submit Question</button>
      </form>

      <div>
        <button onClick={handleSectionReview} className="px-4 py-2 mt-4 text-white bg-blue-500 rounded">Review Question Paper</button>
        {viewQuestions.length > 0 && (
          <div className="mt-4">
            <h2 className="text-lg font-semibold">Questions in Section: {question.section}</h2>
            <ul className="mt-2 space-y-2">
              {viewQuestions.map((q, idx) => (
                <li key={q._id} className="p-2 border rounded">
                  {idx + 1}. {q.questionText}
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}
