// src/pages/SignUp.jsx
import React, { useState } from 'react';
import axios from 'axios';

export default function SignUp() {
  const [form, setForm] = useState({ username: '', password: '', role: 'user' });

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios.post('http://localhost:5000/api/auth/signup', form);
    alert('Signed up!');
  };

  return (
    <form onSubmit={handleSubmit} className="p-4 space-y-4 max-w-sm mx-auto">
      <input className="border p-2 w-full" placeholder="Username" onChange={e => setForm({ ...form, username: e.target.value })} />
      <input className="border p-2 w-full" type="password" placeholder="Password" onChange={e => setForm({ ...form, password: e.target.value })} />
      <select className="border p-2 w-full" onChange={e => setForm({ ...form, role: e.target.value })}>
        <option value="user">User</option>
        <option value="admin">Admin</option>
      </select>
      <button className="bg-blue-500 text-white px-4 py-2 rounded">Sign Up</button>
    </form>
  );
}
