import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Question = () => {
  const [sections, setSections] = useState([]);
  const [sets, setSets] = useState([]);
  const [createNewSet, setCreateNewSet] = useState(false);
  const [newSetName, setNewSetName] = useState('');
  const [newSetTimeLimit, setNewSetTimeLimit] = useState('');
  const [setTimeLimit, setSetTimeLimit] = useState(null);
  const [useCustomTimer, setUseCustomTimer] = useState(false);

  const [form, setForm] = useState({
    category: '',
    section: '',
    set: '',
    questionText: '',
    questionImage: null,
    questionAudio: null,
    options: [
      { type: 'text', content: '' },
      { type: 'text', content: '' },
      { type: 'text', content: '' },
      { type: 'text', content: '' }
    ],
    correctAnswer: 'a',
    timer: null
  });

  const categories = ['Beginner', 'Intermediate', 'Advanced'];
  const sectionMap = {
    Beginner: ['Beginner', 'Intermediate', 'Advanced', 'Pro Advanced'],
    Intermediate: ['Beginner Challenge', 'Intermediate Challenge', 'Advanced Challenge', 'Pro Challenge'],
    Advanced: ['Beginner Level', 'Intermediate Level', 'Advanced Level', 'Pro Level']
  };

  const handleCategoryChange = (e) => {
    const selectedCategory = e.target.value;
    setForm({ ...form, category: selectedCategory, section: '', set: '' });
    setSections(sectionMap[selectedCategory] || []);
    setSets([]);
    setCreateNewSet(false);
  };

  const handleSectionChange = async (e) => {
    const selectedSection = e.target.value;
    setForm(prev => ({ ...prev, section: selectedSection, set: '' }));
    try {
      const res = await axios.get(`http://localhost:3000/api/${form.category}/${selectedSection}/sets`);
      setSets(res.data);
    } catch (err) {
      console.error('Error fetching sets:', err);
    }
  };

  const handleSetChange = async (e) => {
    const selectedSet = e.target.value;
    setForm({ ...form, set: selectedSet });
    try {
      const res = await axios.get(`http://localhost:3000/api/${form.category}/${form.section}/sets`);
      const currentSet = res.data.find(s => s.name === selectedSet);
      setSetTimeLimit(currentSet?.timeLimit || null);
    } catch (err) {
      console.error('Error loading set time:', err);
      setSetTimeLimit(null);
    }
  };

  const createSet = async () => {
    if (!newSetName) return alert("Set name is required");
    try {
      await axios.post(`http://localhost:3000/api/${form.category}/${form.section}/sets`, {
        name: newSetName,
        timeLimit: newSetTimeLimit ? Number(newSetTimeLimit) : null,
      });
      alert("âœ… Set created");
      setCreateNewSet(false);
      setNewSetName('');
      setNewSetTimeLimit('');
      const res = await axios.get(`http://localhost:3000/api/${form.category}/${form.section}/sets`);
      setSets(res.data);
    } catch (err) {
      console.error('Error creating set:', err);
    }
  };

  const updateSetTimeLimit = async () => {
    try {
      await axios.patch(`http://localhost:3000/api/${form.category}/${form.section}/sets/${form.set}/time-limit`, {
        timeLimit: Number(setTimeLimit)
      });
      alert("âœ… Time limit updated");
    } catch (err) {
      console.error("Error updating time limit", err);
    }
  };

  const deleteSetTimeLimit = async () => {
    try {
      await axios.delete(`http://localhost:3000/api/${form.category}/${form.section}/sets/${form.set}/time-limit`);
      setSetTimeLimit(null);
      alert("ðŸ—‘ï¸ Time limit removed");
    } catch (err) {
      console.error("Error deleting time limit", err);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();

    formData.append('category', form.category);
    formData.append('section', form.section);
    formData.append('set', createNewSet ? newSetName : form.set);
    formData.append('questionText', form.questionText);
    if (form.questionImage) formData.append('questionImage', form.questionImage);
    if (form.questionAudio) formData.append('questionAudio', form.questionAudio);
    formData.append('correctAnswer', form.correctAnswer);
    formData.append('timer', useCustomTimer ? form.timer : setTimeLimit);

    form.options.forEach((opt, idx) => {
      formData.append(`options[${idx}][type]`, opt.type);
      formData.append(`options[${idx}][content]`, opt.content);
    });

    try {
      await axios.post('http://localhost:3000/api/questions/create', formData);
      alert('âœ… Question created!');
    } catch (err) {
      console.error('Error creating question:', err);
    }
  };

  return (
    <div className="max-w-4xl p-6 mx-auto bg-white rounded shadow">
      <h2 className="mb-4 text-2xl font-bold">Create Question</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        {/* Category, Section, Set selectors... */}

        {createNewSet && (
          <div>
            <input
              type="text"
              placeholder="Enter set name"
              value={newSetName}
              onChange={(e) => setNewSetName(e.target.value)}
              className="w-full p-2 border rounded"
            />
            <input
              type="number"
              placeholder="Optional time limit in seconds"
              value={newSetTimeLimit}
              onChange={(e) => setNewSetTimeLimit(e.target.value)}
              className="w-full p-2 mt-2 border rounded"
            />
            <div className="flex gap-2 mt-2">
              <button onClick={createSet} type="button" className="px-4 py-2 text-white bg-green-600 rounded">Save</button>
              <button onClick={() => setCreateNewSet(false)} type="button" className="px-4 py-2 text-gray-600 bg-gray-200 rounded">Cancel</button>
            </div>
          </div>
        )}

        {!createNewSet && form.set && (
          <div>
            <p className="text-sm">Set Time Limit: {setTimeLimit ? `${setTimeLimit} seconds` : 'Not set'}</p>
            <input
              type="number"
              value={setTimeLimit || ''}
              onChange={(e) => setSetTimeLimit(e.target.value)}
              placeholder="Update time limit"
              className="w-full p-2 mt-1 border rounded"
            />
            <div className="flex gap-2 mt-2">
              <button onClick={updateSetTimeLimit} type="button" className="px-4 py-1 text-white bg-blue-500 rounded">Update</button>
              <button onClick={deleteSetTimeLimit} type="button" className="px-4 py-1 text-white bg-red-500 rounded">Delete</button>
            </div>
          </div>
        )}

        {/* Remaining form UI for question, image, options, etc. */}
      </form>
    </div>
  );
};

export default Question;
