import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function AdminQuestionForm() {
  const [question, setQuestion] = useState({
    section: '',
    questionType: 'text',
    questionText: '',
    questionImage: '',
    questionAudio: '',
    options: [
      { type: 'text', content: '' },
      { type: 'text', content: '' },
      { type: 'text', content: '' },
      { type: 'text', content: '' }
    ],
    correctAnswer: ''
  });

  const [sections, setSections] = useState([]);
  const [newSection, setNewSection] = useState('');
  const [viewQuestions, setViewQuestions] = useState([]);
  const [summaryMode, setSummaryMode] = useState(false);

  useEffect(() => {
    fetchSections();
  }, []);

  const fetchSections = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/sections');
      setSections(res.data);
    } catch (error) {
      console.error('Error fetching sections:', error);
      alert('Failed to fetch sections');
    }
  };

  const handleOptionChange = (index, field, value) => {
    const newOptions = [...question.options];
    newOptions[index][field] = value;
    setQuestion({ ...question, options: newOptions });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!question.section || !question.correctAnswer) {
      alert('Please fill in all fields');
      return;
    }

    if (!['a', 'b', 'c', 'd'].includes(question.correctAnswer)) {
      alert('Correct answer must be one of a/b/c/d');
      return;
    }

    try {
      await axios.post('http://localhost:5000/api/questions/create', question);
      alert('Question created!');
      setQuestion({
        section: '',
        questionType: 'text',
        questionText: '',
        questionImage: '',
        questionAudio: '',
        options: [
          { type: 'text', content: '' },
          { type: 'text', content: '' },
          { type: 'text', content: '' },
          { type: 'text', content: '' }
        ],
        correctAnswer: ''
      });
      setViewQuestions([]);
    } catch (error) {
      console.error('Error creating question:', error);
      alert('Failed to create question');
    }
  };

  const handleAddSection = async () => {
    if (!newSection.trim()) return;
    try {
      await axios.post('http://localhost:5000/api/sections/create', { name: newSection });
      setNewSection('');
      fetchSections();
    } catch (error) {
      console.error('Error adding section:', error);
      alert('Failed to add section');
    }
  };

  const handleDeleteSection = async (sectionId) => {
    try {
      await axios.delete(`http://localhost:5000/api/sections/${sectionId}`);
      fetchSections();
    } catch (error) {
      console.error('Error deleting section:', error);
      alert('Failed to delete section');
    }
  };

  const handleReviewAllSections = async () => {
    try {
      const promises = sections.map(sec =>
        axios.get(`http://localhost:5000/api/questions/by-section/${sec._id}`)
      );
      const results = await Promise.all(promises);
      const questionsBySection = sections.map((sec, idx) => ({
        sectionName: sec.name,
        questions: results[idx].data
      }));
      setViewQuestions(questionsBySection);
      setSummaryMode(true);
    } catch (error) {
      console.error('Error fetching all sections:', error);
      alert('Failed to fetch question summary');
    }
  };

  const handleFileUpload = (e, type, field) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setQuestion(prev => ({ ...prev, [field]: reader.result }));
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className="p-4 space-y-6">
      {/* Section Management */}
      <div>
        <input
          value={newSection}
          onChange={(e) => setNewSection(e.target.value)}
          className="p-2 border"
          placeholder="New Section Name"
        />
        <button onClick={handleAddSection} className="px-4 py-2 ml-2 text-white bg-purple-600 rounded">
          Add Section
        </button>
        <div className="mt-3 space-y-2">
          {sections.map(sec => (
            <div key={sec._id} className="flex items-center justify-between p-2 border rounded">
              <span>{sec.name}</span>
              <button onClick={() => handleDeleteSection(sec._id)} className="px-3 py-1 text-white bg-red-500 rounded">
                Delete
              </button>
            </div>
          ))}
        </div>
      </div>

      {/* Question Form */}
      <form onSubmit={handleSubmit} className="space-y-4">
        <select
          className="w-full p-2 border"
          value={question.section}
          onChange={(e) => setQuestion({ ...question, section: e.target.value })}>
          <option value="">Select Section</option>
          {sections.map(sec => (
            <option key={sec._id} value={sec._id}>{sec.name}</option>
          ))}
        </select>

        <select
          value={question.questionType}
          onChange={(e) => setQuestion({ ...question, questionType: e.target.value })}
          className="w-full p-2 border"
        >
          <option value="text">Text</option>
          <option value="image">Image</option>
          <option value="audio">Audio</option>
        </select>

        <div>
          <input
            className="w-full p-2 border"
            placeholder={`Enter Question (${question.questionType})`}
            value={question.questionText}
            onChange={(e) => setQuestion({ ...question, questionText: e.target.value })}
          />
          <input
            type="file"
            accept={question.questionType === 'image' ? 'image/*' : 'audio/*'}
            onChange={(e) => handleFileUpload(e, question.questionType, 'questionText')}
          />
        </div>

        {question.options.map((opt, i) => (
          <div key={i} className="flex items-center gap-2">
            <select value={opt.type} onChange={e => handleOptionChange(i, 'type', e.target.value)}>
              <option value="text">Text</option>
              <option value="image">Image</option>
              <option value="audio">Audio</option>
            </select>
            <input
              className="p-2 border"
              placeholder="Option content"
              value={opt.content}
              onChange={e => handleOptionChange(i, 'content', e.target.value)}
            />
            <input
              type="file"
              accept={opt.type === 'image' ? 'image/*' : 'audio/*'}
              onChange={(e) => {
                const file = e.target.files[0];
                if (file) {
                  const reader = new FileReader();
                  reader.onloadend = () => {
                    handleOptionChange(i, 'content', reader.result);
                  };
                  reader.readAsDataURL(file);
                }
              }}
            />
          </div>
        ))}

        <input
          className="w-full p-2 border"
          placeholder="Correct Answer (a/b/c/d)"
          value={question.correctAnswer}
          onChange={e => setQuestion({ ...question, correctAnswer: e.target.value })}
        />

        <button type="submit" className="px-4 py-2 text-white bg-green-500 rounded">
          Submit Question
        </button>
      </form>

      {/* Review Questions Summary */}
      <button
        onClick={handleReviewAllSections}
        className="px-4 py-2 mt-4 text-white bg-blue-500 rounded"
      >
        Review Question Paper
      </button>

      {summaryMode && (
        <div className="mt-4 space-y-4">
          {viewQuestions.map((section, idx) => (
            <div key={idx} className="p-4 border rounded">
              <h3 className="mb-2 text-xl font-semibold">Section: {section.sectionName}</h3>
              {section.questions.length === 0 ? (
                <p className="text-gray-500">No questions available in this section.</p>
              ) : (
                <ul className="space-y-2">
                  {section.questions.map((q, i) => (
                    <li key={q._id} className="p-2 border rounded">
                      <div>{i + 1}. {q.questionText}</div>
                      {q.questionImage && <img src={q.questionImage} alt="Preview" className="w-32 mt-2" />}
                      {q.questionAudio && <audio controls src={q.questionAudio} className="mt-2" />}
                    </li>
                  ))}
                </ul>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
