// src/pages/QuestionPage.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function QuestionPage() {
  const [questions, setQuestions] = useState([]);
  const [answers, setAnswers] = useState({});

  useEffect(() => {
    axios.get('http://localhost:5000/api/questions').then(res => setQuestions(res.data));
  }, []);

  const handleAnswer = (qid, opt) => {
    setAnswers({ ...answers, [qid]: opt });
  };

  const handleSubmit = async () => {
    for (const qid in answers) {
      await axios.post('http://localhost:5000/api/answers', { questionId: qid, selectedOption: answers[qid] });
    }
    alert('Answers submitted!');
  };

  return (
    <div className="p-4 space-y-6">
      {questions.map(q => (
        <div key={q._id} className="border p-4 m rounded shadow">
          <p className="font-semibold mb-2">{q.questionText}</p>
          <div className="space-y-2">
            {q.options.map((opt, i) => (
              <div key={i}>
                {opt.type === 'text' && <button onClick={() => handleAnswer(q._id, String.fromCharCode(97 + i))} className="bg-gray-200 px-4 py-2 rounded">{opt.content}</button>}
                {opt.type === 'image' && <button onClick={() => handleAnswer(q._id, String.fromCharCode(97 + i))}><img src={opt.content} alt={`Option ${i}`} className="w-24 h-24" /></button>}
                {opt.type === 'audio' && <button onClick={() => handleAnswer(q._id, String.fromCharCode(97 + i))}><audio controls src={opt.content}></audio></button>}
              </div>
            ))}
          </div>
        </div>
      ))}
      <button onClick={handleSubmit} className="bg-green-500 text-white px-4 py-2 rounded">Submit Answers</button>
    </div>
  );
}
