// src/pages/AnswerPage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function AnswerPage() {
  const [questions, setQuestions] = useState([]);
  const [selectedAnswers, setSelectedAnswers] = useState({});

  useEffect(() => {
    const fetchQuestions = async () => {
      const res = await axios.get('http://localhost:5000/api/questions');
      setQuestions(res.data);
    };
    fetchQuestions();
  }, []);

  const handleAnswer = (questionId, answer) => {
    setSelectedAnswers(prev => ({ ...prev, [questionId]: answer }));
  };

  const handleSubmit = async () => {
    const user = JSON.parse(localStorage.getItem('user'));
    await axios.post('http://localhost:5000/api/answers', {
      userId: user._id,
      answers: selectedAnswers,
    });
    alert('Answers submitted successfully!');
  };

  return (
    <div className="p-4 space-y-6">
      {questions.map((q, index) => (
        <div key={q._id} className="p-4 border rounded shadow">
          <p className="mb-2 text-xl font-semibold">
            {index + 1}. {q.questionText}
          </p>
          <div className="space-y-2">
            {q.options.map((opt, i) => {
              const optionKey = String.fromCharCode(97 + i);
              const isSelected = selectedAnswers[q._id] === optionKey;

              return (
                <div key={i} className="flex items-center mb-4 space-x-2">
                  <span className="font-bold">{String.fromCharCode(65 + i)}.</span>

                  {opt.type === 'text' && (
                    <button
                      onClick={() => handleAnswer(q._id, optionKey)}
                      className={`w-32 px-4 py-2 text-left rounded ${
                        isSelected ? 'bg-green-500 text-white' : 'bg-gray-200'
                      }`}
                    >
                      {opt.content}
                    </button>
                  )}

                  {opt.type === 'image' && (
                    <button
                      onClick={() => handleAnswer(q._id, optionKey)}
                      className={isSelected ? 'ring-2 ring-green-500 rounded' : ''}
                    >
                      <img src={opt.content} alt={`Option ${i}`} className="w-24 h-24 rounded" />
                    </button>
                  )}

                  {opt.type === 'audio' && (
                    <button
                      onClick={() => handleAnswer(q._id, optionKey)}
                      className={isSelected ? 'ring-2 ring-green-500 rounded' : ''}
                    >
                      <audio controls src={opt.content}></audio>
                    </button>
                  )}
                </div>
              );
            })}
          </div>
        </div>
      ))}
      <button onClick={handleSubmit} className="px-4 py-2 text-white bg-green-500 rounded">
        Submit Answers
      </button>
    </div>
  );
}
