import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function QuestionPage() {
  const [questions, setQuestions] = useState([]);
  const [answers, setAnswers] = useState({});

  useEffect(() => {
    axios.get('http://localhost:5000/api/questions').then(res => setQuestions(res.data));
  }, []);

  const handleAnswer = (qid, opt) => {
    setAnswers(prev => ({ ...prev, [qid]: opt }));
  };

  const handleSubmit = async () => {
    for (const qid in answers) {
      await axios.post('http://localhost:5000/api/answers', {
        questionId: qid,
        selectedOption: answers[qid],
      });
    }
    alert('Answers submitted!');
  };

  return (
    <div className="p-4 space-y-6">
      {questions.map((q, index) => (
        <div key={q._id} className="p-4 border rounded shadow">
          <p className="mb-2 text-xl font-semibold">
            {index + 1}. {q.questionText}
          </p>
          <div className="space-y-2">
            {q.options.map((opt, i) => {
              const optionKey = String.fromCharCode(97 + i); // 'a', 'b'
              const isSelected = answers[q._id] === optionKey;

              return (
                <div key={i} className="flex items-center mb-4 space-x-2">
                  <span className="font-bold">{String.fromCharCode(65 + i)}.</span>

                  {/* TEXT OPTION */}
                  {opt.type === 'text' && (
                    <button onClick={() => handleAnswer(q._id, optionKey)}
                      className={`w-32 px-4 py-2 text-left rounded ${
                        isSelected ? 'bg-green-500 text-white' : 'bg-gray-200' }`}>
                      {opt.content}
                    </button>
                  )}

                  {/* IMAGE OPTION */}
                  {opt.type === 'image' && (
                    <button
                      onClick={() => handleAnswer(q._id, optionKey)}
                      className={`rounded ${
                        isSelected ? 'ring-2 ring-green-500' : ''
                      }`} >
                      <img
                        src={opt.content} // content should be base64 string or URL
                        alt={`Option ${i}`}
                        className="w-24 h-24 rounded"
                      />
                    </button>
                  )}

                  {/* AUDIO OPTION */}
                  {opt.type === 'audio' && (
                    <button
                      onClick={() => handleAnswer(q._id, optionKey)}
                      className={`rounded ${
                        isSelected ? 'ring-2 ring-green-500' : ''
                      }`}
                    >
                      <audio controls src={opt.content} />
                    </button>
                  )}
                </div>
              );
            })}

            <button
              onClick={() => handleAnswer(q._id, null)}
              className="px-4 py-2 mt-2 text-base text-red-700 rounded bg-slate-400"
            >
              Clear
            </button>
            <button
              onClick={() => handleAnswer(q._id, null)}
              className="px-4 py-2 mt-2 ml-4 text-base text-red-600 rounded bg-slate-400"
            >
              Remain
            </button>
          </div>
        </div>
      ))}

      {/* Submission Buttons */}
      <div className="mt-6 space-x-4">
        <button
          onClick={handleSubmit}
          className="px-4 py-2 text-white bg-green-600 rounded hover:bg-green-700"
        >
          Submit Answers
        </button>
        <button
          onClick={handleSubmit}
          className="px-4 py-2 text-white bg-green-600 rounded hover:bg-green-700"
        >
          Review
        </button>
        <button
          onClick={handleSubmit}
          className="px-4 py-2 text-white bg-green-600 rounded hover:bg-green-700"
        >
          Unanswered
        </button>
      </div>
    </div>
  );
}
